Addon.JavascriptInHead:
jQuery( document ).ready(function(){
var ccFormSN,blogId,qs,userid
ccFormSN=jQuery("input[name=ccFormSN]").val();
blogId=jQuery("input[name="+ccFormSN+"blogid]").val();
if(!blogId) {
  blogId=jQuery("select[name="+ccFormSN+"blogid]").val();
}
userId=jQuery("input[name=blogAuthorGroupUserId]").val();
qs='blogId='+blogId+'&ccFormSN='+ccFormSN+'&userid='+userId;
cj.remote({
'method':'blogAuthorGroupSelectorHandler',
'queryString':qs,
'destinationId':'authorSelectCon'
});
})
